async function c(n){const a=new TextEncoder().encode(n),t=await crypto.subtle.digest("SHA-256",a);return Array.from(new Uint8Array(t)).map(e=>e.toString(16).padStart(2,"0")).join("")}function u(n){return n?n.replace(/\D/g,""):""}function m(n){return n?n.toLowerCase().trim():""}async function l(n,r,a){if(!n)throw new Error("companyId is required for application ID generation");const t=m(r),o=u(a);if(!t&&!o){console.warn("[ApplicationId] No email or phone provided, using timestamp fallback");const i=Date.now().toString(36),s=Math.random().toString(36).substring(2,8);return`anon_${i}_${s}`}const e=`${n}:${t}:${o}`;return(await c(e)).substring(0,20)}function p(){const n=new Date().getFullYear(),r=Math.random().toString(36).substring(2,7).toUpperCase();return`SAF-${n}-${r}`}export{p as a,l as g};
